                                    И ТИПЗНАЧЕНИЯ(ЗначенияПоУмолчанию.ТипЗначенийДоступа) = ТИПЗНАЧЕНИЯ(НаборыЗначений.ЗначениеДоступа)
                                    И ЗначенияПоУмолчанию.БезНастройки = ИСТИНА
                            )
                )
          ) И НЕ
    #Иначе
         (
    #КонецЕсли
          ИСТИНА В
          (
